mapper.select:
SELECT * FROM( SELECT rownum cnt, n.* FROM (SELECT * FROM notice n ORDER BY TOLIST DESC) n) WHERE cnt BETWEEN #{start} AND #{end}
and title like '%'||#{title}||'%'
and writer like '%'||#{name}||'%'

mapper.update:
update notice
set counting= counting+1
where no=#{no}

mapper.select:
SELECT count(no)
FROM REPLE
WHERE NO=#{no}
